const deg=10;let rotate=0;const btnPlus=document.getElementById("increase"),btnMinus=document.querySelector(".decrease-button"),btnReset=document.getElementById("reset"),btnText=document.getElementById("value"),btnType=document.getElementById("submit"),div=document.getElementById("square");function rotateSquarePlus(){rotate<360?(rotate+=deg,rotateBoth()):(rotate=360)&&(alert("This is maximum value! Click reset."),rotate=0)}function rotateSquareMinus(){rotate>-360?(rotate-=deg,rotateBoth()):(rotate=-360)&&(alert("This is minimum value! Click reset."),rotate=0)}function clickButton(t){t.preventDefault(),(rotate=btnText.value)>-360&&rotate<360?rotateBoth():(alert("This is minimum value! Click reset."),rotate=0)}function resetRotate(){btnText.value=0,rotate=0,rotateBoth()}function rotateBoth(){document.getElementById("score").value=rotate+" degrees",div.style.transform="rotate("+rotate+"deg)",div.style.transition="0.5s"}btnPlus.addEventListener("click",rotateSquarePlus),btnMinus.addEventListener("click",rotateSquareMinus),btnReset.addEventListener("click",resetRotate),btnType.addEventListener("click",clickButton);